n, k = [int(x) for x in input().split()]
a = [int(x) for x in input().split()]

min_a = min(a)
max_a = max(a)

if min_a == max_a:
    print(0)
    exit()

if k == 1:
    print(0)
    exit()

if k == n:
    print(max_a - min_a)
    exit()

if k < n:
    if max_a - min_a > 1:
        print(max_a - min_a)
    else:
        print(1)

def min_moves(arr, k):
    arr.sort()
    min_val = arr[0]
    max_val = arr[-1]
    if max_val - min_val >= k:
        return -1
    else:
        return max_val - min_val

if __name__ == '__main__':
    n, k = map(int, input().split())
    arr = list(map(int, input().split()))
    print(min_moves(arr, k))
