import sys

def main():
    n = int(sys.stdin.readline().strip())
    for i in range(n):
        s = sys.stdin.readline().strip()
        if n % 2 == 1:
            print(n)
            print(s)
        else:
            good = True
            for i in range(0, n, 2):
                if s[i] == s[i+1]:
                    good = False
                    break
            if good:
                print(0)
                print(s)
            else:
                print(n-1)
                print(s[:n-1])

if __name__ == "__main__":
    main()


def main():
    n = int(input())
    s = input()
    k = 0
    for i in range(0, n, 2):
        if i+1 < n and s[i] == s[i+1]:
            k += 1
    if k == 0:
        print(s)
    else:
        for i in range(0, n, 2):
            if i+1 < n and s[i] == s[i+1]:
                continue
            print(s[i], end="")
        print()

if __name__ == "__main__":
    main()

