n = int(input())
d = list(map(int, input().split()))

d.sort()

if n % 2 == 0:
    mid_index = n // 2
else:
    mid_index = n // 2 + 1

mid = d[mid_index - 1]

count = 0
for i in range(mid_index, n):
    if d[i] == mid:
        count += 1

print(count + 1)

import sys

def main():
    N = int(input())
    d = list(map(int, input().split()))
    d.sort()
    d.reverse()
    ans = 0
    for i in range(N // 2):
        if d[i] == d[i + N // 2]:
            ans += 1
    print(ans)

if __name__ == '__main__':
    main()


