#!/usr/bin/env python3

s = input()
t = input()

if s == t:
    print(0)
else:
    print(len(s) + len(t) - 2*len(set(s) & set(t)))

def solve(s, t):
    s = list(s)
    t = list(t)
    moves = 0
    while len(s) > 0 and len(t) > 0:
        if s[0] == t[0]:
            s.pop(0)
            t.pop(0)
        elif s[0] < t[0]:
            s.pop(0)
            moves += 1
        else:
            t.pop(0)
            moves += 1
    return moves + len(s) + len(t)

if __name__ == '__main__':
    s = input()
    t = input()
    print(solve(s, t))

