    main()


def sum_digits(a):
    s = 0
    while a:
        s += a % 10
        a //= 10
    return s

def main():
    a = int(input())
    while sum_digits(a) % 4 != 0:
        a += 1
    print(a)

if __name__ == '__main__':
    main()


def sum_of_digits(n):
    return sum([int(i) for i in str(n)])

def is_interesting(n):
    return sum_of_digits(n) % 4 == 0 or n % 4 == 0

def next_interesting(n):
    while not is_interesting(n):
        n += 1
    return n

def main():
    n = int(input())
    print(next_interesting(n + 1))

if __name__ == "__main__":
    main()
