# https://atcoder.jp/contests/abc159/tasks/abc159_a

n, k = map(int, input().split())
t = input()

if k == 1:
    print(t)
else:
    print(t * (k - 1) + t[:n - 1])

import sys

def main():
    n, k = map(int, sys.stdin.readline().split())
    t = sys.stdin.readline().strip()
    print(t * (k // n) + t[:k % n])

if __name__ == "__main__":
    main()

