n = int(input())
a = list(map(int, input().split()))

if a[0] != a[n-1]:
    print('NO')
else:
    for i in range(1, n):
        if a[i-1] != a[i]:
            print('NO')
            break
    else:
        print('YES')

def main():
    n = int(input())
    a = [int(i) for i in input().split()]
    a.sort()
    if a[0] == a[-1]:
        print("YES")
    else:
        print("NO")

if __name__ == "__main__":
    main()
