def ravioli_sort(n, a):
    if n <= 1:
        return "YES"
    for i in range(n-1):
        if abs(a[i]-a[i+1]) >= 2:
            return "NO"
    return "YES"

n = int(input())
a = list(map(int, input().split()))

print(ravioli_sort(n, a))


def ravioli_sort(n, arr, count=0):
    if count == n:
        return arr
    for i in range(count, n):
        if i == n-1:
            return ravioli_sort(n, arr, count+1)
        if abs(arr[i]-arr[i+1]) >= 2:
            arr[i], arr[i+1] = arr[i+1], arr[i]
            return ravioli_sort(n, arr, count+1)

n = int(input())
arr = list(map(int, input().split()))

if ravioli_sort(n, arr) == sorted(arr):
    print("YES")
else:
    print("NO")


def is_palindrome(s):
    for i in range(len(s) // 2):
        if s[i] != s[len(s) - i - 1]:
            return False
    return True


def palindrome_index(s):
    for i in range(len(s) // 2):
        if s[i] != s[len(s) - i - 1]:
            if is_palindrome(s[:i] + s[i + 1:]):
                return i
            else:
                return len(s) - i - 1
    return -1


t = int(input())

for _ in range(t):
    s = input()
    print(palindrome_index(s))


