n = int(input())
a = list(map(int, input().split()))

# dp[i] = max number of problems in a valid contest ending at a[i]
dp = [1] * n

for i in range(n):
    for j in range(i):
        if a[j] * 2 >= a[i]:
            break
        dp[i] = max(dp[i], dp[j] + 1)

print(max(dp))


def main():
    n = int(input())
    a = list(map(int, input().split()))
    ans = 1
    cur = a[0]
    for i in range(1, n):
        if a[i] <= cur * 2:
            ans += 1
        else:
            cur = a[i]
    print(ans)


if __name__ == "__main__":
    main()

