def is_complete(a):
    if len(set(a)) == 1:
        return True
    else:
        return False

def is_possible(a):
    if is_complete(a):
        return True
    else:
        for i in range(n-1):
            if a[i] == a[i+1]:
                a[i] += 1
                a[i+1] += 1
                if is_possible(a):
                    return True
                else:
                    a[i] -= 1
                    a[i+1] -= 1
        for i in range(n):
            if a[i] >= 2:
                a[i] -= 2
                if is_possible(a):
                    return True
                else:
                    a[i] += 2
        return False

if is_possible(a):
    print("Yes")
else:
    print("No")

