n = int(input())
a = list(map(int, input().split()))

a.sort()

if n % 2 == 0:
    mid_index = n // 2
else:
    mid_index = n // 2 + 1

mid = a[mid_index - 1]

count = 0
for i in range(mid_index, n + 1):
    if a[i] == mid:
        count += 1

print(count + 1)

