import sys

def solve(a, n, m, rain, umbrellas, umbrellas_sorted):
    r = [0] * (a + 1)
    for l, ri in rain:
        r[l] += 1
        r[ri] -= 1
    for i in range(1, a + 1):
        r[i] += r[i - 1]
    dp = [0] * (a + 1)
    for i in range(1, a + 1):
        dp[i] = dp[i - 1] + r[i - 1]
    for i in range(1, a + 1):
        if i in umbrellas:
            dp[i] = min(dp[i], dp[i - 1] + umbrellas[i])
