memo = {}


def f(n: int, s: str):
    if s in memo.keys():
        return memo.get(s)
    if len(s) == 2 * n:
        return 1
    if len(s) > 2 * n:
        return 0
    if len(s) == 0:
        return 1
    if len(s) == 1:
        return 0
    if s[0] == "(" and s[-1] == ")":
        return f(n, s[1:-1])
    if s[0] == ")":
        return f(n, s[1:])
    if s[-1] == "(":
        return f(n, s[:-1])
    return f(n, s[1:]) + f(n, s[:-1])


