def main():
    n = int(input())
    a = list(map(int, input().split()))
    dp = [0] * n
    dp2 = [0] * n
    dp[0] = 1
    dp2[-1] = 1
    for i in range(1, n):
        if a[i] > a[i - 1]:
            dp[i] = dp[i - 1] + 1
        if a[-i - 1] > a[-i]:
            dp2[-i - 1] = dp2[-i] + 1

    ans = 0
    for i in range(n):
        if i == 0:
            ans = max(ans, dp[i + 1])
        elif i == n - 1:
            ans = max(ans, dp2[i - 1])
        else:
            ans = max(ans, dp[i + 1] + dp2[i - 1] - 1)
    print(ans)


if __name__ == "__main__":
    main()

def main():
    n = int(input())
    a = list(map(int, input().split()))
    print(solve(n, a))

def solve(n, a):
    dp = [1 for _ in range(n)]
    for i in range(n):
        for j in range(i):
            if a[j] < a[i]:
                dp[i] = max(dp[i], dp[j] + 1)
    return max(dp)

