n, x, y = map(int, input().split())
n = list(input())[::-1]

if n[x-y-1] == '0':
    n[x-y-1] = '1'
else:
    n[x-y-1] = '0'
    for i in range(x-y, x):
        if n[i] == '1':
            n[i] = '0'
            break
        else:
            n[i] = '0'

print(n.count('0'))


def solve(n, x, y, number):
    operations = 0
    for i in range(n-x+1):
        if number[i] != number[i+x-y]:
            operations += 1
    return operations

def main():
    n, x, y = map(int, input().split())
    number = input()
    print(solve(n, x, y, number))

if __name__ == "__main__":
    main()


n, x, y = map(int, input().split())
n = list(map(int, input()))

if n[y-1] == 0:
    n[y-1] = 1
else:
    n[y-1] = 0
    for i in range(y+1, x):
        if n[i-1] == 0:
            n[i-1] = 1
            break
        else:
            n[i-1] = 0

print(n.count(0))


def solve(n, x, y, number):
    operations = 0
    for i in range(n-x+1):
        if number[i] != number[i+x-y-1]:
            operations += 1
    return operations

def main():
    n, x, y = map(int, input().split())
    number = input()
    print(solve(n, x, y, number))

if __name__ == "__main__":
    main()
