def main():
    n = int(input())
    a = list(map(int, input().split()))

    ans = 0
    for i in range(n):
        if a[i] % 2 == 1:
            ans += 1
    print("first" if ans % 2 == 1 else "second")


if __name__ == "__main__":
    main()


