def get_input():
    n, r = map(int, input().split())
    a = list(map(int, input().split()))
    return n, r, a

def solve(n, r, a):
    ans = 0
    i = 0
    while i < n:
        if a[i] == 0:
            j = i + 1
            while j < n and a[j] == 0:
                j += 1
            if j - i > 2*r:
                return -1, a
            if j < n:
                a[j] = 1
            ans += 1
            i = j + 1
        else:
            i += 1
    return ans, a

def main():
    n, r, a = get_input()
    ans, a = solve(n, r, a)
    print(ans)

if __name__ == '__main__':
    main()

import sys

def main():
    n, r = map(int, sys.stdin.readline().split())
    a = list(map(int, sys.stdin.readline().split()))
    heaters = []
    for i in range(n):
        if a[i] == 1:
            heaters.append(i)
    if len(heaters) == 0:
        print(-1)
        return
    heaters.append(n)
    heaters.insert(0, -r)
    i = 0
    ans = 0
    while i < len(heaters) - 1:
        if heaters[i] + r - 1 >= heaters[i + 1]:
            heaters.pop(i)
        else:
            i += 1
            ans += 1
    print(ans)

if __name__ == '__main__':
    main()


