def solve(n, r, a):
    ans = 0
    i = 0
    while i < n:
        if a[i] == 0:
            j = i + 1
            while j < n and a[j] == 0:
                j += 1
            if j - i > 2*r:
                return -1
            a[j-1] = 1
            ans += 1
            i = j
