import sys

n = int(sys.stdin.readline())

a = list(map(int, sys.stdin.readline().split()))

a.sort(reverse=True)

b = []

for i in range(n):
    b.append(a.pop(0))
    if a and a[0] * 2 == b[-1]:
        b.append(a.pop(0))

print(' '.join(map(str, b)))


n = int(input())
a = list(map(int, input().split()))

if n == 2:
    print(a[1], a[0], sep=' ')
elif n == 3:
    if a[0] * 3 == a[1] and a[1] * 2 == a[2]:
        print(a[0], a[1], a[2], sep=' ')
    else:
        print(a[2], a[0], a[1], sep=' ')
else:
    for i in range(n):
        if a[i] % 3 == 0:
            a[i] //= 3
        else:
            a[i] *= 2
    a.sort()
    print(*a, sep=' ')

