    n = int(input())  # number of elements
    a = list(map(int, input().split()))  # list of elements
    for i in range(n-1):
        if a[i] == a[i+1]:
            a[i] += 1
            a[i+1] += 1
    if len(set(a)) == 1 and a[0] != 0:
        print("YES")
    else:
        print("NO")

