def main():
    n, k = map(int, input().split())  # n:数列の長さ, k:基準値
    A = list(map(int, input().split()))  # 数列
    i, j = 0, n - 1
    ans = 0
    while i <= j:
        if A[i] > k and A[j] > k:
            break
        elif A[i] <= k and A[j] <= k:
            ans += 2
            i += 1
            j -= 1
        elif A[i] <= k:
            ans += 1
            i += 1
        else:
            ans += 1
            j -= 1
    print(ans)

if __name__ == '__main__':
    main()

def main():
    n, k = map(int, input().split())  # n:問題数 k:難易度
    problems = list(map(int, input().split()))
    ans = 0  # 回答可能問題数
    while len(problems) > 0:
        if problems[0] <= k:  # 先頭の問題が解ける
            ans += 1
            problems.pop(0)
        elif problems[-1] <= k:  # 末尾の問題が解ける
            ans += 1
            problems.pop()
        else:
            break
    print(ans)


