# Solution

n = int(input())  # number of test cases

for i in range(n):
    s = input()  # input string
    ok = True
    for j in range(1, len(s)):
        if ord(s[j]) == ord(s[j - 1]) + 1:
            ok = False
            break
    if len(s) != len(set(s)):
        ok = False
    print('Yes' if ok else 'No')


