import sys


def solve(n, m):
    if n == 1:
        return 0
    elif n == 2:
        return m
    else:
        return (m - n + 1) * 2


if __name__ == '__main__':
    t = int(sys.stdin.readline())
    for _ in range(t):
        n, m = map(int, sys.stdin.readline().split())
        print(solve(n, m))
def main():
    n = int(input())
    arr = list(map(int, input().split()))
    for i in range(n):
        if i % 2 == 0:
            print(arr[i], end=' ')


if __name__ == '__main__':
    main()


