def main():
    n, a, b = map(int, input().split())
    s = list(map(int, input().split()))
    ans = 0
    i = 0
    while i < n:
        if s[i] == 1 and a < b and b > 0:
            a += 1
            b -= 1
            ans += 1
        elif s[i] == 1 and b > 0:
            b += 1
            ans += 1
        elif a > 0:
            a -= 1
            ans += 1
        elif b > 0:
            b -= 1
            ans += 1
        else:
            break
        i += 1
    print(ans)

if __name__ == "__main__":
    main()


