    for i in range(n):  # проходим по всем элементам
        if a[i] % 3 == 0:  # если элемент делится на 3 без остатка
            a[i] //= 3
        else:  # если нет
            a[i] *= 2
    a.sort()  # сортируем список
    print(*a)  # выводим список


