# Solution

n, k = map(int, input().split())  # input
a = list(map(int, input().split()))  # input

if k == 1:
    print(max(a))  # output
else:
    print(max(a) - min(a) + 1)  # output
import math

n, k = map(int, input().split())
a = list(map(int, input().split()))

ans = 0
for i in range(n):
    if a[i] % 2 == 1:
        a[i] -= 1
        ans += 1

if sum(a) // k < 2:
    print(ans)
else:
    print(ans + math.ceil(sum(a) / k) - 1)
