import sys

def main():
    n = int(input())
    if n % 2 == 0:
        print(n)
    else:
        print(n * 2)

if __name__ == '__main__':
    main()
import sys

def main():
    n = int(sys.stdin.readline())
    num = n
    while True:
        if num % 2 == 0:
            print(num)
            break
        num += n

if __name__ == '__main__':
  main()

