def main():
    N, K = map(int, input().split())
    A = list(map(int, input().split()))
    while K > 0:
        K -= 1
        if N % 2 == 0:
            A = A[N//2:] + A[:N//2]
        else:
            A = A[N//2+1:] + A[:N//2+1]
    print(*A)

if __name__ == '__main__':
    main()

N = int(input())
H = list(map(int, input().split()))

ans = 0
curr = 0
for i in range(N - 1):
    if H[i + 1] <= H[i]:
        curr += 1
    else:
        ans = max(ans, curr)
        curr = 0
print(max(ans, curr))
