import sys

def get_input():
    return [int(x) for x in sys.stdin.readline().split()]


def solve(n: int, m: int) -> int:
    if n == m:
        return 0
    if n > m:
        return -1
    if m % n != 0:
        return -1
    return solve(n * 2, m) + 1 if m % (n * 2) == 0 else solve(n * 3, m) + 1


