import sys

n = int(sys.stdin.readline())

if n == 0:
    print(0)
    exit()

if n > 0:
    res = []
    while n > 0:
        res.append(str(n % 2))
        n //= 2
    print("".join(res[::-1]))
    exit()

# n < 0
n = -n

res = []
while n > 0:
    if n % 2 == 0:
        res.append("1")
        n = (n // 2) + 1
    else:
        res.append("0")
        n //= 2

print("1" + "".join(res[::-1]))


def f(n):
    if n == 0:
        return 0
    if n % 2 == 0:
        return f(n // 2)
    return f(n // 2) + 1

if f(n) == 0:
    print(0)
    exit()

ans = ""
while n != 0:
    if n % (-2) == 0:
        ans += "0"
        n = n // (-2)
    else:
        ans += "1"
        n = n // (-2) + 1

print(ans[::-1])
