def main():
    H, W = map(int, input().split())
    h, w = map(int, input().split())
    print(H*W-(H-h)*(W-w))  # 全体の面積から重なっている部分の面積を引く

if __name__ == '__main__':
    main()

def calc_remaining_cells(H, W, h, w):  # 引数は全て整数
    return (H-h)*(W-w)  # 小麦の数を返す

H, W = map(int, input().split())
h, w = map(int, input().split())

print(calc_remaining_cells(H, W, h, w))

