def main():
    n, k = map(int, input().split())
    if(n == 1 and k == 1):
        print("YES")
        print("1")
    elif(n < k):
        print("NO")
    elif(n > k):
        a = [1]
        for i in range(k - 1):
            a.append(2 * a[i])
        if(sum(a) == n):
            print("YES")
            print(" ".join(map(str, a)))
        else:
            print("NO")
    else:
        print("NO")

if __name__ == "__main__":
    main()

def find_min_arr(n, k):
    arr = [1] * k
    for i in range(k-1, -1, -1):
        if sum(arr) > n:
            arr[i] -= sum(arr) - n
        if arr[i] == 0:
            return False
        if i == 0:
            break
        if arr[i] > arr[i-1] * 2:
            arr[i-1] = arr[i] // 2
    return arr

n, k = [int(x) for x in input().split()]
arr = find_min_arr(n, k)
if arr:
    print("YES")
    print(*arr)
else:
    print("NO")
