def is_lucky_number(number):
    if len(number) % 2 == 0:
        half_len = len(number) // 2
        first_half = number[:half_len]
        second_half = number[half_len:]
        if sum(int(digit) for digit in first_half) == sum(int(digit) for digit in second_half)
            return True
        else
            return False
    else
        return False

