    for i in range(len(a)):
        if a[i] == sum(a[:i]) + sum(a[i+1:]):
            return True
    return False

def solve(a):
    if is_good(a):
        return 1
    res = 0
    for i in range(len(a)):
        res += solve(a[:i] + a[i+1:])
