    for i in range(n):
        projects.append(list(map(int, input().split())))

    projects.sort(key=lambda x: x[1], reverse=True)

    for i in range(n):
        if projects[i][0] > r:
            print("NO")
            sys.exit()
        r += projects[i][1]

    print("YES")

