n = int(input())

if n == 1:
    print(0)
    exit()

moves = 0

while n > 1:
    moves += 1
    if n % 2 == 0:
        n = n // 2
    else:
        n += 1

print(moves)
def min_moves(n):
    x, y = 1, 1
    moves = 0
    while True:
        moves += 1
        if n == x * y:
            return moves
        elif n < x * y:
            y -= 1
            x += 1
        else:
            x += 1


def main():
    print(min_moves(int(input())))


if __name__ == '__main__':
    main()


def min_moves(n):
    x, y = 1, n
    moves = 0
    while True:
        moves += 1
        if x * y == n:
            break
        elif x * y > n:
            y -= 1
            x += 1
        else:
            x += 1


def main():
    n = int(input())
    print(min_moves(n) + 1)


if __name__ == '__main__':
    main()

