N = int(input())
H = list(map(int, input().split()))

ans = 0
curr = 0
for i in range(N-1):
    if H[i+1] <= H[i]:
        curr += 1
    else:
        ans = max(ans, curr)
        curr = 0
print(max(ans, curr))

def main():
    n = int(input())
    h = list(map(int, input().split()))
    ans = 0
    while n > 0:
        n -= 1
        if n == 0:
            ans += 1
            break
        if h[n] >= h[n - 1]:
            ans += 1
        else:
            break
    print(ans)

if __name__ == '__main__':
    main()

def main():
    n = int(input())
    h = list(map(int, input().split()))
    ans = 0
    while n > 1:
        if h[n-1] >= h[n-2]:
            h[n-2] = h[n-1]
            n -= 1
            ans += 1
        else: break
    ans += 1
    print(ans)

if __name__ == '__main__':
