from collections import deque

def bfs(graph, start):
    visited = {}
    q = deque()
    q.append(start)
    visited[start] = True
    while q:
        v = q.popleft()
        for w in graph[v]:
            if w not in visited:
                visited[w] = True
                q.append(w)
    return len(visited)


