            print(math.sqrt(p * (p - a) * (p - b) * (p - c)))
    elif b >= a and b >= c:
        if a >= c:
            p = (a + b + c) / 2
            print(math.sqrt(p * (p - a) * (p - b) * (p - c)))
        else:
            p = (a + b + c) / 2
            print(math.sqrt(p * (p - a) * (p - b) * (p - c)))
    else:
        if a >= b:
            p = (a + b + c) / 2
            print(math.sqrt(p * (p - a) * (p - b) * (p - c)))
        else:
            p = (a + b + c) / 2
            print(math.sqrt(p * (p - a) * (p - b) * (p - c)))

