def max_difference(n, m):
    if n == 1:
        return 0
    else:
        return max(2 * m - n, n - 2 * m)

def main():
    t = int(input())
    for _ in range(t):
        n, m = map(int, input().split())
        print(max_difference(n, m))

if __name__ == '__main__':
    main()


def main():
    n = int(input())
    a = list(map(int, input().split()))
    a.sort()
    c = 0
    i = 0
    while i < n:
        if a[i] == a[i + 1]:
            c += 1
            i += 2
        else:
            i += 1
    print(c)

if __name__ == '__main__':
    main()

