def dfs(i, j):
    if board[i][j] == 0:
        return 0
    board[i][j] = 0
    count = 1
    for di in range(-1, 2):
        for dj in range(-1, 2):
            if di == 0 and dj == 0:
                continue
            ni = i + di
            nj = j + dj
            if ni < 0 or ni >= N or nj < 0 or nj >= M:
                continue
            count += dfs(ni, nj)
    return count

