# Solution

n, k = map(int, input().split())
a = list(map(int, input().split()))

if k == 1:
    print(max(a))
else:
    print(max(a) - min(a) + 1)

n, k = map(int, input().split())
a = list(map(int, input().split()))

ans = 0
for i in range(n):
    if a[i] % 2 == 1:
        a[i] -= 1
        ans += 1

print(ans + (sum(a) - 1) // k)

n, k = map(int, input().split())
a = list(map(int, input().split()))

ans = 0
for i in range(n):
    if a[i] % 2 != 0:
        a[i] -= 1
        ans += 1

if sum(a) // k < 2:
    print(ans)
else:
    print(ans + sum(a) // k - 1)

n, k = map(int, input().split())
a = list(map(int, input().split()))

ans = 0
for i in range(n):
    if a[i] % 2 == 1:
        a[i] -= 1
        ans += 1

if sum(a) // k < 2:
    print(n)
else:
    print(ans + sum(a) // k - 1)

