def min_length(n, words):
    count = 1
    for i in range(n - 1):
        if words[i] != words[i + 1]:
            count += 1

    return count

n = int(input())
words = input().split()

print(min_length(n, words))


def main():
    n = int(input())
    words = [x for x in input().split()]  # 入力を分割してリスト化
    if n == 1:
        print(len(words[0]))
        quit()
    ans = len("".join(words))
    for i in range(n):
        for j in range(i+1, n):
            if words[i] == words[j]:
                tmp = words[:i] + [words[i][0].upper()] + words[j+1:]
                ans = min(len("".join(tmp)), ans)
    print(ans)

