import sys


def is_prime(n):
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    for i in range(3, int(n ** 0.5) + 1, 2):
        if n % i == 0:
            return False
    return True


for line in sys.stdin:
    n = int(line)
    if is_prime(n):
        print('Prime')
    else:
        print('Not prime')


