def get_max_seq(l):
    d = {}
    m = 0
    for i in l:
        if i in d:
            continue
        d[i] = 1
        for j in range(32):
            if i + 2**j in d:
                d[i + 2**j] = d[i] + 1
            if i - 2**j in d:
                d[i - 2**j] = d[i] + 1
            m = max(m, d[i])
    return m, d



