import sys
import math

def main():
    n = int(input())
    s = input()
    l = len(s)
    dp = [[0] * (n + 1) for i in range(l + 1)]
    ans = 0
    for i in range(1, l + 1):
        if s[i] == '(':
            dp[i][0] = 1
    for i in range(1, l + 1):
        for j in range(n + 1):
            if dp[i][j] == 0:
                continue
            if s[i] == '(':
                dp[i + 1][j] += dp[i][j]
                dp[i + 1][j + 1] += dp[i][j]
            else:
                if j > 0:
                    dp[i + 1][j - 1] += dp[i][j]
    for i in range(n + 1):
        ans += dp[l][i] 
    print(ans)

if __name__ == "__main__":
    main()


n = int(input())
s = input()

memo = {}

def f(n, s):
    if s in memo:
        return memo[s]
    if len(s) == 2*n:
        return 1
    if len(s) > 2*n:
        return 0
    if len(s) == 0:
        return 1
    if len(s) == 1:
        return 0
    if (s[0] == '(' and s[-1] == ')'):
        return f(n, s[1:-1])
    if (s[0] == ')'):
        return f(n, s[1:])
    if (s[-1] == '('):
        return f(n, s[:-1])
    return f(n, s[1:]) + f(n, s[:-1])

print(f(n, s) % 1000000007)


n = int(input())
s = input()

memo = {}

def f(n, s):
    if s in memo:
        return memo[s]
    if len(s) == 2*n:
        return 1
    if len(s) > 2*n:
        return 0
    if len(s) == 0:
        return 1
    if len(s) == 1:
        return 0
    ret = 0
    if s[0] == '(':
        ret += f(n, s[1:])
    if s[-1] == ')':
        ret += f(n, s[:-1])
    memo[s] = ret
    return ret

print(f(n, s) % 1000000007)


