def main():
    n = int(input())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))
    ans = -1
    for i in range(n - 1):
        if a[i] >= b[i]:
            i += 1
            continue
        elif a[i] < b[i] and a[i + 1] >= b[i] - a[i]:
            a[i + 1] -= b[i] - a[i]
        elif a[i] < b[i] and a[i + 1] < b[i] - a[i]:
            ans = i + 1
            break
    print(ans + 1)

if __name__ == '__main__':
    main()


def get_input():
    n = int(input())
    a = [int(x) for x in input().split()] + [0] * (n - len(input().split()))
    return n, a

def solve(n, a):
    ans = 0
    for i in range(n):
        if a[i] == 0:
            ans += 1
            j = i
            while j <= n and a[j] == 0:
                a[j] = 1
                j += 1
    return ans

def main():
    n, a = get_input()
    ans = solve(n, a)
    print(ans)

if __name__ == '__main__':
    main()
import sys

def main():
    n, r = map(int, sys.stdin.readline().split())
    a = list(map(int, sys.stdin.readline().split()))
    heaters = []
    for i in range(n):
        if a[i] == 1:
            heaters.append(i)
    if len(heaters) == 0:
        print(-1)
        return
    heaters.append(n)
    heaters.insert(0, -r)
    i = 0
    ans = 0
    while i < len(heaters) - 1:
        if heaters[i] + r - 1 >= heaters[i + 1]:
            heaters.pop(i)
        else:
            i += 1
            ans += 1
    print(ans)

if __name__ == '__main__':
    main()
