def main():
    n, m = map(int, input().split())
    a = list(map(int, input().split()))  # a list of prices
    offers = []
    for i in range(m):
        x, y, c = map(int, input().split())
        offers.append([x-1, y-1, c])  # subtract 1 since we use 0-based index

    min_cost = 0
    for i in range(n):
        min_cost += a[i]

    for offer in offers:
        x, y, c = offer[0], offer[1], offer[2]
        if a[x] + a[y] > c:
            min_cost -= a[x] + a[y]
            min_cost += c

    print(min_cost)


if __name__ == "__main__":
    main()

def main():
    n, m = map(int, input().split())
    a = list(map(int, input().split()))
    g = [[] for _ in range(n + 1)]
    for _ in range(m):
        x, y, w = map(int, input().split())
        g[x].append((y, w))
        g[y].append((x, w))

    spt = [float('inf')] * (n + 1)
    spt[1] = 0
    q = [1]
    while q:
        u = q.pop(0)
        for v, w in g[u]:
            if spt[v] > spt[u] + w:
                spt[v] = spt[u] + w
                q.append(v)

    ans = sum(a)
    for i in range(2, n + 1):
        ans += min(spt[i], a[i - 1])

    print(ans)


