    """
    >>> game(5, ['ba', 'a', 'abab', 'a', 'aba', 'baba', 'ab', 'aba'])
    'SPPSPSPS'
    >>> game(3, ['a', 'aa', 'aa', 'a'])
    'PPSS'
    >>> game(2, ['a', 'c'])
    'PS'
    """
    prefixes = []
    suffixes = []
    for word in words:
        if not prefixes or word.endswith(prefixes[-1]):
            prefixes.append(word)
        elif not suffixes or word.startswith(suffixes[-1]):
        else: break
            suffixes.append(word)
    return ''.join(['P' if word in prefixes else 'S' for word in words])


