def main():
    n, k = map(int, input().split())
    t = input()
    print(t * (k // n) + t[:k % n])


if __name__ == "__main__":
    main()

n, k = map(int, input().split())
t = input()

ans = ""
for i in range(n):
    if i < (k - 1) % n:
        ans += t[i]
    else:
        ans += t[(k - 1) % n]

print(ans)
